import{_ as t,c as e,o as r,a2 as n}from"./chunks/framework.B8ScJZFy.js";const x=JSON.parse('{"title":"HTTP Protocol","description":"","frontmatter":{},"headers":[],"relativePath":"Application Layer/HTTP protocol.md","filePath":"Application Layer/HTTP protocol.md","lastUpdated":1718164289000}'),l={name:"Application Layer/HTTP protocol.md"},s=n('<h1 id="http-protocol" tabindex="-1">HTTP Protocol <a class="header-anchor" href="#http-protocol" aria-label="Permalink to &quot;HTTP Protocol&quot;">‚Äã</a></h1><h2 id="hypertext-transfer-protocol-Ë∂ÖÊñáÊú¨‰º†ÈÄÅÂçèËÆÆ" tabindex="-1"><strong>HyperText Transfer Protocol(Ë∂ÖÊñáÊú¨‰º†ÈÄÅÂçèËÆÆ)</strong> <a class="header-anchor" href="#hypertext-transfer-protocol-Ë∂ÖÊñáÊú¨‰º†ÈÄÅÂçèËÆÆ" aria-label="Permalink to &quot;**HyperText Transfer Protocol(Ë∂ÖÊñáÊú¨‰º†ÈÄÅÂçèËÆÆ)**&quot;">‚Äã</a></h2><p>client/server model</p><ul><li>client: <strong>browser</strong> that requests, receives and &quot;displays&quot; the web objects</li><li>server: web server sends web objects in response to requests</li></ul><p>transport protocol - <strong>TCP</strong><br> HTTP 1.0 - <strong>RFC 1945</strong><br> HTTP 1.1 - <strong>RFC 2068</strong><br> Port - <strong>80</strong></p><h2 id="distingulish" tabindex="-1">Distingulish <a class="header-anchor" href="#distingulish" aria-label="Permalink to &quot;Distingulish&quot;">‚Äã</a></h2><table><thead><tr><th style="text-align:center;">HTTP 1.0</th><th style="text-align:center;">HTTP 1.1</th></tr></thead><tbody><tr><td style="text-align:center;">a TCP tranmit one object</td><td style="text-align:center;">TCP transmit multiple objects</td></tr></tbody></table><h2 id="stateless" tabindex="-1"><strong>Stateless</strong> <a class="header-anchor" href="#stateless" aria-label="Permalink to &quot;**Stateless**&quot;">‚Äã</a></h2><blockquote><p>server maintains no information about past client requests</p></blockquote><ul><li>Non-persistent <ul><li><strong>HTTP/1.0</strong></li><li>server parses requests, responses, and close TCP connections</li><li>2 RTTs(Round Trip Time) to fetch each object</li><li>slow start for each object transfer</li></ul></li><li>Persistent</li><li>default for <strong>HTTP/1.1</strong></li><li>on same TCP connection(see as above)</li><li>client sends requests for all referenced objects as soon as it receives base HTML</li><li><strong>Fewer RTTs</strong></li><li><strong>Less slow start</strong></li></ul><h2 id="http-message-format" tabindex="-1"><strong>HTTP message format</strong> <a class="header-anchor" href="#http-message-format" aria-label="Permalink to &quot;**HTTP message format**&quot;">‚Äã</a></h2><ul><li><strong>Request</strong><ul><li>request line <blockquote><p>method URL version</p></blockquote><ul><li>method <table><thead><tr><th style="text-align:center;">method</th><th style="text-align:center;">introduction</th><th style="text-align:center;">usage</th></tr></thead><tbody><tr><td style="text-align:center;">GET</td><td style="text-align:center;">Requests a representation of the specified resource</td><td style="text-align:center;">only retrieves data</td></tr><tr><td style="text-align:center;">HEAD</td><td style="text-align:center;">Asks for a response identical to a GET request, but without the response body</td><td style="text-align:center;">often retrieves the meta information of resource</td></tr><tr><td style="text-align:center;">POST</td><td style="text-align:center;">Submits an entity to the specified resource</td><td style="text-align:center;">often causes a change in state or side effects on the server</td></tr><tr><td style="text-align:center;">PUT</td><td style="text-align:center;">Replaces all current representations of the target resource with the request payload</td><td style="text-align:center;">often updates the resource that existed or provides new resource</td></tr><tr><td style="text-align:center;">DELETE</td><td style="text-align:center;">Deletes the specified resource</td><td style="text-align:center;">deletes resource</td></tr><tr><td style="text-align:center;">CONNECT</td><td style="text-align:center;">Establishes a tunnel to the server identified by the target resource</td><td style="text-align:center;">often for proxy servers</td></tr><tr><td style="text-align:center;">OPTIONS</td><td style="text-align:center;">Describes the communication options for the target resource</td><td style="text-align:center;">requests for available options</td></tr><tr><td style="text-align:center;">TRACE</td><td style="text-align:center;">Performs a message loop-back test along the path to the target resource</td><td style="text-align:center;">often for tests and diagnoses</td></tr><tr><td style="text-align:center;">PATCH</td><td style="text-align:center;">Applies partial modifications to a resource</td><td style="text-align:center;">often for tests and diagnoses</td></tr></tbody></table></li><li>version <table><thead><tr><th style="text-align:center;">version</th></tr></thead><tbody><tr><td style="text-align:center;">HTTP/1.0</td></tr><tr><td style="text-align:center;">HTTP/1.1</td></tr><tr><td style="text-align:center;">HTTP/2.0</td></tr></tbody></table></li></ul></li><li>header line<strong>s</strong>(multiple lines formatted the same as below) <blockquote><p>header field name:value<br> header field name:value<br> ...</p></blockquote><ul><li>header field name <table><thead><tr><th style="text-align:center;">header field name</th><th style="text-align:center;">introduction</th></tr></thead><tbody><tr><td style="text-align:center;">Accept</td><td style="text-align:center;">Informs the server about the types of data that can be sent back</td></tr><tr><td style="text-align:center;">Accept-Encoding</td><td style="text-align:center;">The encoding algorithm, usually a compression algorithm, that can be used on the resource sent back</td></tr><tr><td style="text-align:center;">Accept-Language</td><td style="text-align:center;">Informs the server about the human language the server is expected to send back</td></tr><tr><td style="text-align:center;">Connection</td><td style="text-align:center;">Controls whether the network connection stays open after the current transaction finishes</td></tr><tr><td style="text-align:center;">Host</td><td style="text-align:center;">Specifies the domain name of the server (for virtual hosting), and (optionally) the TCP port number on which the server is listening</td></tr><tr><td style="text-align:center;">Referer</td><td style="text-align:center;">The address of the previous web page from which a link to the currently requested page was followed</td></tr><tr><td style="text-align:center;">User-Agent</td><td style="text-align:center;">Contains a characteristic string that allows the network protocol peers to identify the application type, operating system, software vendor or software version of the requesting software user agent</td></tr><tr><td style="text-align:center;">Cookie</td><td style="text-align:center;">Contains stored HTTP cookies previously sent by the server with the Set-Cookie header</td></tr><tr><td style="text-align:center;">Cache-Control</td><td style="text-align:center;">Directives for caching mechanisms in both requests and responses</td></tr></tbody></table></li></ul></li><li>extra carriage return and line feed</li><li>Entity body <blockquote><p>data here......</p></blockquote></li></ul></li><li><strong>Response</strong><ul><li>status line <blockquote><p>version status_code status_description</p></blockquote><ul><li><p>status codes</p><table><thead><tr><th style="text-align:center;">status code class</th><th style="text-align:center;">introduction</th></tr></thead><tbody><tr><td style="text-align:center;">1xx</td><td style="text-align:center;">Informational responses</td></tr><tr><td style="text-align:center;">2xx</td><td style="text-align:center;">Successful responses</td></tr><tr><td style="text-align:center;">3xx</td><td style="text-align:center;">Redirection messages</td></tr><tr><td style="text-align:center;">4xx</td><td style="text-align:center;">Client error responses</td></tr><tr><td style="text-align:center;">5xx</td><td style="text-align:center;">Server error responses</td></tr></tbody></table><table><thead><tr><th style="text-align:center;">status code</th><th style="text-align:center;">description</th></tr></thead><tbody><tr><td style="text-align:center;">100</td><td style="text-align:center;">Continue</td></tr><tr><td style="text-align:center;">101</td><td style="text-align:center;">Switching Protocols</td></tr><tr><td style="text-align:center;">200</td><td style="text-align:center;">OK üòÉ</td></tr><tr><td style="text-align:center;">202</td><td style="text-align:center;">Accepted</td></tr><tr><td style="text-align:center;">204</td><td style="text-align:center;">No Content</td></tr><tr><td style="text-align:center;">205</td><td style="text-align:center;">Reset Content</td></tr><tr><td style="text-align:center;">206</td><td style="text-align:center;">Partial Content</td></tr><tr><td style="text-align:center;">301</td><td style="text-align:center;">Moved Permanently üò¶</td></tr><tr><td style="text-align:center;">302</td><td style="text-align:center;">Found</td></tr><tr><td style="text-align:center;">304</td><td style="text-align:center;">Not Modified</td></tr><tr><td style="text-align:center;">307</td><td style="text-align:center;">Temporary Redirect</td></tr><tr><td style="text-align:center;">308</td><td style="text-align:center;">Permanent Redirect</td></tr><tr><td style="text-align:center;">400</td><td style="text-align:center;">Bad Request</td></tr><tr><td style="text-align:center;">403</td><td style="text-align:center;">Forbidden üò¶</td></tr><tr><td style="text-align:center;">404</td><td style="text-align:center;">Not Found üò¶</td></tr><tr><td style="text-align:center;">405</td><td style="text-align:center;">Method Not Allowed</td></tr><tr><td style="text-align:center;">406</td><td style="text-align:center;">Not Acceptable</td></tr><tr><td style="text-align:center;">500</td><td style="text-align:center;">Internal Server Error</td></tr><tr><td style="text-align:center;">501</td><td style="text-align:center;">Not Implemented</td></tr><tr><td style="text-align:center;">502</td><td style="text-align:center;">Bad Gateway</td></tr><tr><td style="text-align:center;">505</td><td style="text-align:center;">HTTP Version Not Supported</td></tr></tbody></table></li></ul></li><li>header line<strong>s</strong>(multiple lines formatted the same as below) <blockquote><p>header field name:value<br> header field name:value<br> ...</p></blockquote><ul><li>header field name <table><thead><tr><th style="text-align:center;">header field name</th><th style="text-align:center;">introduction</th></tr></thead><tbody><tr><td style="text-align:center;">Date</td><td style="text-align:center;">Contains the date and time at which the message was originated</td></tr><tr><td style="text-align:center;">Content-Type</td><td style="text-align:center;">Indicates the media type of the resource</td></tr><tr><td style="text-align:center;">Content-Length</td><td style="text-align:center;">The size of the resource, in decimal number of bytes</td></tr><tr><td style="text-align:center;">Content-Encoding</td><td style="text-align:center;">Used to specify the compression algorithm</td></tr><tr><td style="text-align:center;">Last-Modified</td><td style="text-align:center;">The last modification date of the resource, used to compare several versions of the same resource</td></tr><tr><td style="text-align:center;">Set-Cookie</td><td style="text-align:center;">Send cookies from the server to the user-agent</td></tr></tbody></table></li></ul></li><li>extra carriage return and line feed</li><li>data <blockquote><p>data here...</p></blockquote></li></ul></li></ul><h2 id="web-caches" tabindex="-1"><strong>Web Caches</strong> <a class="header-anchor" href="#web-caches" aria-label="Permalink to &quot;**Web Caches**&quot;">‚Äã</a></h2><blockquote><p>satisfy client request without involving origin server</p></blockquote><ul><li><strong>web accesses via web cache</strong></li><li>client sends all http requests to web cache <ul><li>If objects at web cache, web cache immediately returns the objects in http response</li><li>Else requests objects from the origin server, then returns http response to client</li></ul></li><li>Advantages <ul><li><strong>smaller response time</strong></li><li><strong>decrease traffic to distant servers</strong></li></ul></li></ul>',15),a=[s];function o(i,d,c,g,h,y){return r(),e("div",null,a)}const p=t(l,[["render",o]]);export{x as __pageData,p as default};
